import React, { useEffect, useState } from 'react'
import "./Admin.css";
import axios from 'axios';

function Addcours() {
  const [cours,setCours] = useState({
    name:"",
    price:"",
    img: "",
    typeid:"",
    authorid: "",
    descriptions: "",
  });
  const [author,setAuthor] = useState([])
  const [type,setType] = useState([])

  useEffect(()=>{
    const fecthAllUser = async () =>{
        try{
            const res = await axios.get("http://localhost:8800/author")
            setAuthor(res.data);

            const res1 = await axios.get("http://localhost:8800/type")
            setType(res1.data);
        }catch(err){
            console.log(err)
        }
    }
    fecthAllUser()
},[])


  const handleChange = (e) =>{
    setCours((prev)=>({...prev,[e.target.name]: e.target.value}))
  };


  const handlClick = async e =>{
    e.preventDefault()
    try{
      await axios.post("http://localhost:8800/course", cours)
    }catch(err){

    }
  }

  return (
    <div className='mainvin'>
        <h1>Добавить курс</h1>
        <input type='text' onChange={handleChange} placeholder='Названия' name='name'/>
        <input type='text' onChange={handleChange} placeholder='Цена' name='price'/>

        <select size="1" name="typeid">
        {type.map((ty) => (
        <option value={ty.ID} onChange={handleChange} key={ty.ID} >{ty.name}</option>
        ))}
        </select>

        <select size="1" name="authorid">
        {author.map((aut) => (
        <option value={aut.ID} onChange={handleChange} key={aut.ID}>{aut.name}</option>
        ))}
        </select>
        
        <input type='text' onChange={handleChange} placeholder='Описания' name='descriptions' />
        <input type='file' onChange={handleChange} placeholder='Обложка курса' name='img'/>
        <button className='butnext' onClick={handlClick}>Добавить</button>
    </div>
  )
}

export default Addcours
