import React, { useEffect, useState } from 'react';
import "./Headercss.css"
import axios from 'axios';
function Header() {

  const [user,setUser] = useState([])

  useEffect(()=>{
    const fecthAllUser = async () =>{
        try{
          const res = await axios.get("http://localhost:8800")
          setUser(res.data.email)
          const res_all = res.data.valid;
        }catch(err){
            console.log(err)
        }
    }
    fecthAllUser()

    if(res_all){
      return (
        <header>
          <div className="logo">
            <a className='main scale' href="/">Jemos</a>
          </div>
          <div>
            <a className='info scale' href='/info'> О нас</a>
          </div>
          <div className="register">
            <a className="reg scale">{user}</a>
          </div>
        </header>
      );
    }else{
      return (
        <header>
          <div className="logo">
            <a className='main scale' href="/">Jemos</a>
          </div>
          <div>
            <a className='info scale' href='/info'> О нас</a>
          </div>
          <div className="register">
            <a className="reg scale" href='/register' >Регистрация</a>
            <div className="reg scale" >{}</div>
          </div>
        </header>
      );
    }



},[])




  
}

export default Header;
