  const [user,setUser] = useState({
    name: "",
    email:"",
    password:"",
    role:"use",
    date_start_akk: "",
  });

  const nav = useNavigate();
  
  const handleChange = (e) =>{
    setUser((prev)=>({...prev,[e.target.name]: e.target.value}))
  };


  const handlClick = async e =>{
    e.preventDefault()
    try{
      await axios.post("http://localhost:8800/user", user)
      nav("/login");
    }catch(err){

    }
  }
  
