import React, { useEffect, useState } from 'react';
import {Link} from "react-router-dom"
import axios from "axios"
import './App.css';


function App() {
  const [course,setСourse] = useState([])

  useEffect(()=>{
    const fecthAllUser = async () =>{
        try{
            const res = await axios.get("http://localhost:8800/course")
            setСourse(res.data);
        }catch(err){
            console.log(err)
        }
    }
    fecthAllUser()
},[])

  return (
    <div className='mainwin'>
     <a className='text'>
      <div>{course.map((cour) => (
        <div className='onecourse'>
          <div>{cour.name}</div>
          <div>{cour.price}</div>
          <button><Link className='butupdata' to={`/course/${cour.ID}`}>Узанть больше о курсе</Link></button>   
          </div>
      ))}</div>
      </a>
    </div>
  );
}

export default App;
