import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";
import './App.css';

function App() {
  const [course, setCourse] = useState([]);
  const [value, setValue] = useState('');
  const [sortType, setSortType] = useState('all');

  axios.defaults.withCredentials = true;
  useEffect(() => {
    const fetchAllUser = async () => {
      try {
        const res = await axios.get("http://localhost:8800/course");
        setCourse(res.data);
        const res2 = await axios.get("http://localhost:8800");
        if (res2.data.valid) {
          // Valid user
        } else {
          // nav("/login");
        }
      } catch (err) {
        console.log(err);
      }
    }
    fetchAllUser();
  }, []);

  const handleSortChange = (event) => {
    setSortType(event.target.value);
  };

  const filteredCourse = course
    .filter(cours => cours.name.toLowerCase().includes(value.toLowerCase()))
    .filter(cours => sortType === 'all' || cours.type === sortType);

  return (
    <div>
      <header>
        <input
          type="text"
          className='poisk'
          onChange={(event) => setValue(event.target.value)}
          placeholder="Поиск..."
        />

          <select onChange={handleSortChange} value={sortType}>
            <option value="all">All</option>
            <option value="javascript">Type 1</option>
            <option value="Photoshop">Type 2</option>
            <option value="HTML">Type 3</option>
          </select>
      </header>
      <main>
        <div className='allcours'>
          {filteredCourse.map((cour) => (
            <div className='onecourse' key={cour.ID}>
              <div className='namecours'>{cour.name}</div>
              <div className='price'>Цена: {cour.price} руб.</div>
              <div><img className='imgcours' src={`http://localhost:8800/course/image/${cour.ID}`} alt={cour.name} /></div>
              <button className='linkcours'>
                <Link to={`/course/${cour.ID}`}>Узнать больше о курсе</Link>
              </button>
            </div>
          ))}
        </div>
      </main>
    </div>
  );
}

export default App;
