import React, { useEffect, useState } from 'react';
import "./buycours.css"
import axios from 'axios';
import {useLocation, useNavigate } from 'react-router-dom';

function Buycours() {


  const [valid,setValid] = useState([])
    const [userid,setUserid] = useState([]) 
    const [course,setCourse] = useState([]) 


  const [buycourse,setBuycourse] = useState({
    id_course: "",
    id_user: "",
    date: "",//new Date().toISOString().slice(0, 19).replace('T', ' ')
  });



  axios.defaults.withCredentials = true;
  useEffect(()=>{
    const fecthAllUser = async () =>{
        try{
          const res = await axios.get("http://localhost:8800")
          const res1 = await axios.get("http://localhost:8800/course/" + courseId)
          setCourse(res1.data);
          setValid(res.data.valid);
          setUserid(res.data.ID);

        }catch(err){
            console.log(err)
        }
    }
    fecthAllUser()

// Устанавливаем текущую дату и время при загрузке компонента
const currentDate = new Date();
const formattedDate = currentDate.getFullYear() + '-' + 
                      String(currentDate.getMonth() + 1).padStart(2, '0') + '-' + 
                      String(currentDate.getDate()).padStart(2, '0') + ' ' + 
                      String(currentDate.getHours()  + 3).padStart(2, '0') + ':' + 
                      String(currentDate.getMinutes()).padStart(2, '0') + ':' + 
                      String(currentDate.getSeconds()).padStart(2, '0');
setBuycourse(prevState => ({
  ...prevState,
  date: formattedDate
}));



},[])

const location = useLocation();
  const queryParams = new URLSearchParams(location.search);
  const courseId = queryParams.get('courseId');

useEffect(() => {
    
    if (userid && courseId) {
      setBuycourse(prevState => ({
        ...prevState,
        id_course: courseId,
        id_user: userid
      }));
    }
  }, [userid, courseId]);


  
    
   


const buycors = async e =>{
    e.preventDefault()
    try{
        const response = await axios.post("http://localhost:8800/buycourse", buycourse)
        console.log('Response:', response.data);    
        
      }catch(err){
  
      }
}

if(valid){
    return (
        <div className="checkout-form-container">


          <h2>Введите данные карты</h2>
          <div>{course.name}</div>
          <form  className="checkout-form">
            <div className="form-group">
              <label htmlFor="cardNumber">Номер карты</label>
              <input
                type="text"
                id="cardNumber"
                required
              />
            </div>
            <div className="form-group">
              <label htmlFor="expiryDate">Дата окончания карты (MM/YY)</label>
              <input
                type="text"
                id="expiryDate"
                required
              />
            </div>
            <div className="form-group">
              <label htmlFor="cvv">CVV</label>
              <input
                type="text"
                id="cvv"
                required
              />
            </div>
            <div className="form-group">
              <label htmlFor="cardName">Имя на карте</label>
              <input
                type="text"
                id="cardName"
                required
              />
            </div>
            <button onClick={buycors} type="submit" className="submit-button">КУПИТЬ</button>
          </form>
        </div>
      );
  }else{
    return (
     <div>
          Войди в акаунт
      </div>
    );
  }
}
export default Buycours;
