import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import './Admin.css';

function Admin() {
  const [user, setUser] = useState([]);
  const [author, setAuthor] = useState([]);
  const [type, setType] = useState([]);
  const [course, setCourse] = useState([]);
  const [admtrue, setAdmtrue] = useState([]);
  const [buy, setBuy] = useState([]);
  const [buydate, setBuydate] = useState([]);
  const [search, setSearch] = useState('');

  axios.defaults.withCredentials = true;
  
  useEffect(() => {
    const fetchAllUser = async () => {
      try {
        const res = await axios.get('http://localhost:8800/author');
        const res2 = await axios.get('http://localhost:8800');
        const res3 = await axios.get('http://localhost:8800/buy_course');
        const res4 = await axios.get('http://localhost:8800/date_course');
        setBuydate(res4.data);
        setBuy(res3.data);
        setAdmtrue(res2.data.role);
        setAuthor(res.data);
      } catch (err) {
        console.log(err);
      }
    };
    fetchAllUser();
  }, []);

  useEffect(() => {
    const fetchAllUser = async () => {
      try {
        const res = await axios.get('http://localhost:8800/User');
        setUser(res.data);
      } catch (err) {
        console.log(err);
      }
    };
    fetchAllUser();
  }, []);

  useEffect(() => {
    const fetchAllUser = async () => {
      try {
        const res = await axios.get('http://localhost:8800/type');
        setType(res.data);
      } catch (err) {
        console.log(err);
      }
    };
    fetchAllUser();
  }, []);

  useEffect(() => {
    const fetchAllUser = async () => {
      try {
        const res = await axios.get('http://localhost:8800/course');
        setCourse(res.data);
      } catch (err) {
        console.log(err);
      }
    };
    fetchAllUser();
  }, []);

  const handleDelete = async (ID) => {
    try {
      await axios.delete(`http://localhost:8800/User/${ID}`);
      window.location.reload();
    } catch (err) {
      console.log(err);
    }
  };

  const handleDeleteAuthor = async (ID) => {
    try {
      await axios.delete(`http://localhost:8800/author/${ID}`);
      window.location.reload();
    } catch (err) {
      console.log(err);
    }
  };

  const handleDeleteType = async (ID) => {
    try {
      await axios.delete(`http://localhost:8800/type/${ID}`);
      window.location.reload();
    } catch (err) {
      console.log(err);
    }
  };

  const handleDeleteCourse = async (ID) => {
    try {
      await axios.delete(`http://localhost:8800/course/${ID}`);
      window.location.reload();
    } catch (err) {
      console.log(err);
    }
  };

  const handleDeleteBuy = async (ID) => {
    try {
      await axios.delete(`http://localhost:8800/buy/${ID}`);
      window.location.reload();
    } catch (err) {
      console.log(err);
    }
  };

  const getUserName = (id) => {
    const use1 = user.find((use) => use.ID === id);
    return use1 ? use1.name : 'Unknown User';
  };

  const getCourseTitle = (id) => {
    const cour = course.find((courses) => courses.ID === id);
    return cour ? cour.name : 'Unknown Course';
  };

  const getDateCourse = (id) => {
    const cour = course.find((courses) => courses.ID === id);
    return cour ? cour.date : 'Unknown Course';
  };

  const handleSearch = (e) => {
    setSearch(e.target.value);
  };

  const filteredUsers = user.filter((u) => u.email.toLowerCase().includes(search.toLowerCase()));

  if (admtrue === 'adm') {
    return (
      <div className='mainvin'>
        <div className='admin-section'>
          <b>–ü–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–∏</b>
          <input
            type='text'
            placeholder='–ü–æ–∏—Å–∫ –ø–æ email'
            value={search}
            onChange={handleSearch}
            className='search-bar'
          />
        </div>
        <div className='text'>
          {filteredUsers.map((use) => (
            <div className='alluse' key={use.ID}>
              <a>{use.ID} = {use.email} {use.password} {use.role}</a>
              <button onClick={() => handleDelete(use.ID)}>del</button>
              <button><Link className='butupdata' to={`/updatauser/${use.ID}`}>updata</Link></button>
            </div>
          ))}
        </div>
        <div className='admin-section'><b>–ê–≤—Ç–æ—Ä—ã –∫—É—Ä—Å–æ–≤</b></div>
        <div className='text'>
          {author.map((aut) => (
            <div className='alluse' key={aut.ID}>
              <a>{aut.ID} = {aut.name} {aut.surname} {aut.descriptions}</a>
              <button onClick={() => handleDeleteAuthor(aut.ID)}>del</button>
              <button><Link className='butupdata' to={`/updataauthor/${aut.ID}`}>updata</Link></button>
            </div>
          ))}
