import React, { useEffect, useState } from 'react';
import {Link} from "react-router-dom"
import axios from "axios"
import "./Admin.css";

function Admin() {
    const [user,setUser] = useState([])
    
    useEffect(()=>{
        const fecthAllUser = async () =>{
            try{
                const res = await axios.get("http://localhost:8800/user")
                setUser(res.data);
            }catch(err){
                console.log(err)
            }
        }
        fecthAllUser()
    },[])
    
    const hendelDelete = async (id)=>{
        try{
            await axios.delete("http://localhost:8800/user"+id)
            window.location.reload()
            console.log(id)
        }catch(err){
            console.log(err)
        }
    } 

    return (
    <div className='mainvin'>
       <div className='text'>Админ</div>
       <div className="text">{user.map(use=>(
        <div className="alluse" key={user.ID}>
            <a>{use.email}</a>
            <button onClick={() => hendelDelete(user.id)}>del</button>
            <button>updata</button>
        </div>
       ))}</div>
       <button className='butadd scale'><Link className='butadd' to={"/addcours"}>Добвить курс</Link></button>
    </div>
    )
}

export default Admin
