import React, { useEffect, useState } from 'react';
import "./Headercss.css"
import axios from 'axios';
function Header() {

  const [user,setUser] = useState([])
  const [valid,setValid] = useState([])

  useEffect(()=>{
    const fecthAllUser = async () =>{
        try{
          const res = await axios.get("http://localhost:8800")
          setUser(res.data.email)
          setValid(res.data.valid)
        }catch(err){
            console.log(err)
        }
    }
    fecthAllUser()
},[])
    const Logout = () =>{
      window.localStorage.removeItem("isLogedIn")
      document.cookie = "username=; expires=Thu, 01 Jan 1970 00:00:00 UTC; path=/;";
    }


    if(valid){
      return (
        <header>
          <div className="logo">
            <a className='main scale' href="/">Jemos</a>
          </div>
          <div>
            <a className='info scale' href='/info'> О нас</a>
          </div>
          <div className="register">
            <a className="reg scale">{user}</a>
          </div>
          <div>
          <button className="scale" onClick={() => Logout()} >ВЫХОД</button>
          </div>
        </header>
      );
    }else{
      return (
        <header>
          <div className="logo">
            <a className='main scale' href="/">Jemos</a>
          </div>
          <div>
            <a className='info scale' href='/info'> О нас</a>
          </div>
          <div className="register">
            <a className="reg scale" href='/register' >Регистрация</a>
            
          </div>
        </header>
      );
    }

  
}

export default Header;
