import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";
import './App.css';

function App() {
  const [courses, setCourses] = useState([]);
  const [courseTypes, setCourseTypes] = useState([]); // состояние для типов курсов
  const [value, setValue] = useState('');
  const [courseType, setCourseType] = useState(''); // состояние для выбранного типа курса

  axios.defaults.withCredentials = true;

  useEffect(() => {
    const fetchAllCourses = async () => {
      try {
        const res = await axios.get("http://localhost:8800/course");
        setCourses(res.data);
        const res2 = await axios.get("http://localhost:8800");
        if (res2.data.valid) {
          // Пользователь валидирован
        } else {
          // nav("/login"); // Перенаправление на страницу входа при необходимости
        }
      } catch (err) {
        console.log(err);
      }
    };
    fetchAllCourses();

    const fetchCourseTypes = async () => {
      try {
        const res = await axios.get("http://localhost:8800/course/types"); // Эндпоинт для получения типов курсов
        setCourseTypes(res.data);
      } catch (err) {
        console.log(err);
      }
    };
    fetchCourseTypes();
  }, []);

  const filteredCourses = courses.filter(course => {
    return (
      course.name.toLowerCase().includes(value.toLowerCase()) &&
      (courseType === '' || course.id_type === courseType) // фильтрация по типу курса
    );
  });

  return (
    <div>
      <div className='mainwin12'>
        <header>
          <input
            type="text"
            className='poisk'
            onChange={(event) => setValue(event.target.value)}
            placeholder="Поиск..."
          />
          <select onChange={(event) => setCourseType(event.target.value)} value={courseType}>
            <option value="">Все типы</option>
            {courseTypes.map((type) => (
              <option key={type.id} value={type.id}>{type}</option>
            ))}
          </select>
        </header>
        <main>
          <div className='allcours'>
            {filteredCourses.map((course) => (
              <div className='onecourse' key={course.ID}>
                <div><img className='imgcours2' src={`http://localhost:8800/course/image/${course.ID}`} alt={course.name} /></div>
                <div className='namecours2'>{course.name}</div>
                <div className='price'>{course.price} руб. <button className='linkcours'><Link to={`/course/${course.ID}`}>Узнать больше о курсе</Link></button></div>
              </div>
            ))}
          </div>
        </main>
      </div>
    </div>
  );
}

export default App;
