},[])

  return (
<div>
            <header>
                <input
                    type="text" className='poisk' onChange={(event) => setValuse(event.target.value)} placeholder="Поиск..."/>
            </header>
            <main>
                <div className='allcours'>{filteredCourse.map((cour) => (
                  <div className='onecourse' key={cour.ID}>
                    <div className='namecours'>{cour.name}</div>
                    <div className='price'>Цена: {cour.price} руб.</div>
                    <div><img className='imgcours' src={`http://localhost:8800/course/image/${cour.ID}`} alt={course.name} /></div>
                    <button className='linkcours'><Link  to={`/course/${cour.ID}`}>Узанть больше о курсе</Link></button>   
                  </div>
              ))}</div>
            </main>
        </div>
);
}

export default App;
