import React, { useEffect } from 'react'
import { useState } from 'react';
import axios from 'axios';
import "./userupdata.css"
import { useLocation, useNavigate } from 'react-router-dom';

function Userupdata() {
 const [user,setUser] = useState({
        email:"",
        password:"",
        role:"use"
      });
      const [oneuser,setOneuser] = useState([])

      const navigate = useNavigate();
      const location = useLocation()
      const userid = location.pathname.split("/")[2];

      useEffect(()=>{
        const fecthAllUser = async () =>{
            try{
                const res = await axios.get("http://localhost:8800/User/" + userid)
                setOneuser(res.data);
            }catch(err){
                console.log(err)
            }
        }
        fecthAllUser()
    },[])



      

      const handleChange = (e) =>{
        setUser((prev)=>({...prev,[e.target.name]: e.target.value}))
      };
    
    
      const handlClick = async e =>{
        e.preventDefault()
        try{
          await axios.put("http://localhost:8800/User/" + userid, user)
        }catch(err){
          console.log(err)
        }
      }



  return (
    <div className='mainvin'>
      <div>
      {oneuser.map((ouse)=>(
      <div className="alluse" key={ouse.ID}>
      <a>{ouse.ID}{ouse.email} {ouse.password} {ouse.role}</a>
      </div>
        ))}
      </div>


      <h1>Обновить данные пользователя </h1>
      <input type='text' placeholder='Почта' onChange={handleChange} name='email'/><br/>
      <input type='text' placeholder='Пароль' onChange={handleChange} name='password' /><br/>
      <input type='text' placeholder='Роль' onChange={handleChange} name='role' /><br/>
      <button  className='butnext' href='/' onClick={handlClick}>Обновить</button>
      </div>
  )
}

export default Userupdata
