import React, { useEffect, useState, useRef } from 'react'
import "./Admin.css";
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function Addcours() {
  const [cours,setCours] = useState({
    name:"",
    price:"",
    date: "",
    ID_type:"",
    ID_author: "",
    descriptions: "",
  });

  const filePick = useRef(null);
  const [img,setImg] = useState([])

  const nav = useNavigate();

  const [author,setAuthor] = useState([])
  const [type,setType] = useState([])

  useEffect(()=>{
    const fecthAllUser = async () =>{
        try{
            const res = await axios.get("http://localhost:8800/author")
            setAuthor(res.data);

            const res1 = await axios.get("http://localhost:8800/type")
            setType(res1.data);
        }catch(err){
            console.log(err)
        }
    }
    fecthAllUser()
},[])


  const handleChange = (e) =>{
    setCours((prev)=>({...prev,[e.target.name]: e.target.value}))
    console.log(e.target.files[0]);
  };


  const handlClick = async e =>{
    e.preventDefault()
    cours["typeid"] = document.getElementsByName("typeid")[0].value
    cours["authorid"] = document.getElementsByName("authorid")[0].value
    try{
      console.log(cours)
      await axios.post("http://localhost:8800/course", cours)
      
      nav("/admin");
    }catch(err){

    }
  }

  const handlpick = () =>{
   // const formdata = new FormData();
     // formdata.append('img', file);
      //axios.post("http://localhost:8800/upload", formdata)
    filePick.current.click();
  }


  return (
    <div className='mainvin'>
        <h1>Добавить курс</h1>
        <input type='text' onChange={handleChange} placeholder='Названия' name='name'/>
        <input type='text' onChange={handleChange} placeholder='Цена' name='price'/>

        <select onChange={handleChange} size="1" name="typeid">
        {type.map((ty) => (
            <option key={ty.ID} value={ty.ID}> {ty.name} </option>
        ))}
        </select>

        <input type='date' onChange={handleChange} name='date'/>

        <select onChange={handleChange} size="1" name="authorid">
        {author.map((aut) => (
            <option key={aut.ID} value={aut.ID}> {aut.name} </option>
        ))}
        </select>

        <input type='text' onChange={handleChange} placeholder='Описания' name='descriptions' />

       

        <button className='butnext' onClick={handlClick}>Добавить</button>
    </div>
  )
}

export default Addcours


