import React, { useEffect, useState } from 'react'
import "./OneCoursecss.css"
import axios from 'axios';
import { useLocation, Link } from 'react-router-dom';
import { useParams, useNavigate } from 'react-router-dom';

function OneCourse() {
  const [course, setCourse] = useState([])
  const [author, setAuthor] = useState([])


  const location = useLocation();
  const courseid = location.pathname.split("/")[2];

  axios.defaults.withCredentials = true;
  useEffect(()=>{
    const fecthAllUser = async () =>{
        try{
            const res = await axios.get("http://localhost:8800/course/" + courseid)
            setCourse(res.data);
            const res1 = await axios.get("http://localhost:8800/author")
            setAuthor(res1.data)
        }catch(err){
            console.log(err)
        }
    }
    fecthAllUser()
},[])

const getAuthor = (id) => {
  const aut1 = author.find((aut) => aut.ID === id);
  return aut1 ? aut1.name : 'Unknown User';
};

  const history = useNavigate();

  const handleBuyCourse = () => {
    history(`/buycours?courseId=${courseid}`);  // Перенаправляем на страницу покупки с query параметром
  }


    return (   
        <div className='mainvin'>
        {course.map((cour) => (
          <div key={cour.ID}>
            <div style={{fontSize: "56px"}}>{cour.name}</div>
            <div style={{fontSize: "24px"}}>{cour.price} рублей</div>
            <div>{cour.descriptions}</div>
            <div>Автор курса: <button><Link className='butupdata' to={`/authorpage/${cour.ID_author}`}>{getAuthor(cour.ID_author)}</Link></button>   </div>
            
            <button onClick={handleBuyCourse}>купить</button>
        </div>
        ))}
      </div>
    )
}

export default OneCourse
