import axios from 'axios';
import React, { useState } from 'react'
import "./updata.css"

function updatacours() {
  
  const [user,setUser] = useState({
    email:"",
    password:"",
    role:""
  });

  const handleChange = (e) =>{
    setUser((prev)=>({...prev,[e.target.name]: e.target.value}))
  };


  const handlClick = async e =>{
    e.preventDefault()
    try{
      await axios.post("http://localhost:8800/user",user)
    }catch(err){

    }
  } 

  return (
     <div className='mainwin'>
      <h1>Изменения данных Пользователя </h1>
      <input type='text' placeholder='Почта' onChange={handleChange} name='email'/><br/>
      <input type='text' placeholder='Пароль' onChange={handleChange} name='password' /><br/>
      <button onClick={handlClick}>Обновить</button>
      </div>
  )
}

export default updatacours
