import React, { useEffect, useState } from 'react';
import "./Admin.css";
import axios from 'axios';
import { useNavigate, useParams } from 'react-router-dom';

function Updatacourse() {
  const [course, setCourse] = useState({
    name: "",
    price: "",
    date: "",
    ID_type: "",
    ID_author: "",
    descriptions: "",
  });

  const [img, setImg] = useState(null);
  const navigate = useNavigate();
  const { id } = useParams();
  const [author, setAuthor] = useState([]);
  const [type, setType] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const courseRes = await axios.get(`http://localhost:8800/course/${id}`);
        setCourse(courseRes.data);
        const authorsRes = await axios.get("http://localhost:8800/author");
        setAuthor(authorsRes.data);
        const typesRes = await axios.get("http://localhost:8800/type");
        setType(typesRes.data);
      } catch (err) {
        console.log(err);
      }
    };
    fetchData();
  }, [id]);

  const handleChange = (e) => {
    setCourse((prev) => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleFileChange = (e) => {
    setImg(e.target.files[0]);
  };

  const handleClick = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("name", course.name);
    formData.append("price", course.price);
    formData.append("date", course.date);
    formData.append("ID_type", course.ID_type);
    formData.append("ID_author", course.ID_author);
    formData.append("descriptions", course.descriptions);
    if (img) {
      formData.append("img", img);
    }

    try {
      await axios.put(`http://localhost:8800/course/${id}`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });
      navigate("/admin");
    } catch (err) {
      console.log(err);
    }
  };

  return (
    <div className='mainvin2'>
      <h1>Редактировать курс</h1>
      <div className='form-group'>
        <input type='text' onChange={handleChange} placeholder='Название' name='name' value={course.name} />
      </div>
      <div className='form-group'>
        <input type='text' onChange={handleChange} placeholder='Цена' name='price' value={course.price} />
      </div>
      <div className='form-group'>
        <select onChange={handleChange} name="ID_type" value={course.ID_type}>
          <option value="" disabled>Выберите тип курса</option>
          {type.map((ty) => (
            <option key={ty.ID} value={ty.ID}> {ty.name} </option>
          ))}
        </select>
      </div>
      <div className='form-group'>
        <input type='date' onChange={handleChange} name='date' value={course.date} />
      </div>
      <div className='form-group'>
        <select onChange={handleChange} name="ID_author" value={course.ID_author}>
          <option value="" disabled>Выберите автора</option>
          {author.map((aut) => (
            <option key={aut.ID} value={aut.ID}> {aut.name} </option>
          ))}
        </select>
      </div>
      <div className='form-group'>
        <input type='text' onChange={handleChange} placeholder='Описание' name='descriptions' value={course.descriptions} />
      </div>
      <div className='form-group'>
        <input type="file" onChange={handleFileChange} />
      </div>
      <button className='butnext' onClick={handleClick}>Обновить</button>
    </div>
  );
}

export default Updatacourse;
