import React, { useEffect, useState } from 'react';
import {Link} from "react-router-dom"
import axios from "axios"
import "./Admin.css";

function Admin() {
    //—á—Ç–µ–Ω–∏—è –±–¥
    const [user,setUser] = useState([])
    const [author,setAuthor] = useState([])
    const [type,setType] = useState([])
    const [course,set–°ourse] = useState([])
    const [admtrue,setAdmtrue] = useState([])
    const [buy,setBuy] = useState([])
    

    axios.defaults.withCredentials = true;
    useEffect(()=>{
        const fecthAllUser = async () =>{
            try{
                const res = await axios.get("http://localhost:8800/author")
                const res2 = await axios.get("http://localhost:8800")
                const res3 = await axios.get("http://localhost:8800/buy_course")
                setBuy(res3.data)
                setAdmtrue(res2.data.role);
                setAuthor(res.data);
            }catch(err){
                console.log(err)
            }
        }
        fecthAllUser()
    },[])


    useEffect(()=>{
        const fecthAllUser = async () =>{
            try{
                const res = await axios.get("http://localhost:8800/User")
                setUser(res.data);
            }catch(err){
                console.log(err)
            }
        }
        fecthAllUser()
    },[])

    useEffect(()=>{
        const fecthAllUser = async () =>{
            try{
                const res = await axios.get("http://localhost:8800/type")
                setType(res.data);
            }catch(err){
                console.log(err)
            }
        }
        fecthAllUser()
    },[])

    useEffect(()=>{
        const fecthAllUser = async () =>{
            try{
                const res = await axios.get("http://localhost:8800/course")
                set–°ourse(res.data);
            }catch(err){
                console.log(err)
            }
        }
        fecthAllUser()
    },[])

    //—É–¥–∞–ª–µ–Ω–∏—è 
    const hendelDelete = async (ID)=> {
        try{
            await axios.delete("http://localhost:8800/User/"+ID)
            console.log(ID)
            window.location.reload()
            
        }catch(err){
            console.log(err)
        }
    } 

    const hendelDeleteauthor = async (ID)=> {
        try{
            await axios.delete("http://localhost:8800/author/"+ID)
            console.log(ID)
            window.location.reload()
            
        }catch(err){
            console.log(err)
        }
    } 

    const hendelDeletetype = async (ID)=> {
        try{
            await axios.delete("http://localhost:8800/type/"+ID)
            console.log(ID)
            window.location.reload()
            
        }catch(err){
            console.log(err)
        }
    } 

    const hendelDeletecourse = async (ID)=> {
        try{
            await axios.delete("http://localhost:8800/course/"+ID)
            console.log(ID)
            window.location.reload()
            
        }catch(err){
            console.log(err)
        }
    } 

    const hendelDeletebuy = async (ID)=> {
        try{
            await axios.delete("http://localhost:8800/buy/"+ID)
            console.log(ID)
            window.location.reload()
            
        }catch(err){
            console.log(err)
        }
    } 
    


    //–∫–ª–∏–µ–Ω—Ç
   if(admtrue === "adm"){
    return (
        <div className='mainvin'>
           <div className='admin-section'><b>–ü–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–∏</b></div>
                <div className="text">{user.map((use)=>(
                    <div className="alluse" key={use.ID}>
                        <a>{use.ID} = {use.email} {use.password} {use.role}</a>
                        <button onClick={() =>hendelDelete(use.ID)}>del</button>
                        <button><Link className='butupdata' to={`/updatauser/${use.ID}`}>updata</Link></button>
                    </div>
                ))}
                </div>
                
                <div className='admin-section'><b>–ê–≤—Ç–æ—Ä—ã –∫—É—Ä—Å–æ–≤</b></div>
                <div className="text">{author.map((aut)=>(
                    <div className="alluse" key={aut.ID}>
                        <a>{aut.ID} = {aut.name} {aut.surname} {aut.descriptions}</a>
                        <button onClick={() =>hendelDeleteauthor(aut.ID)}>del</button>
                        <button><Link className='butupdata' to={`/updataauthor/${aut.ID}`}>updata</Link></button>   
                    </div>
                ))}
                </div>
                <button className='butadd scale'><Link className='butadd' to={"/addauthor"}>–î–æ–±–≤–∏—Ç—å –∞–≤—Ç–æ—Ä–∞</Link></button>
    
                <div className='admin-section'><b>–¢–∏–ø –∫—É—Ä—Å–∞</b></div>
                <div className="text">{type.map((ty)=>(
                    <div className="alluse" key={ty.ID}>
                        <a>{ty.ID} = {ty.name} </a>
                        <button onClick={() =>hendelDeletetype(ty.ID)}>del</button>
                        <button><Link className='butupdata' to={`/updatatype/${ty.ID}`}>updata</Link></button>   
                    </div>
                ))}
                </div>
                <button className='butadd scale'><Link className='butadd' to={"/addtype"}>–î–æ–±–≤–∏—Ç—å —Ç–∏–ø–∞ –∫—É—Ä—Å–∞</Link></button>
    
                <div className='admin-section'><b>–ö—É—Ä—Å—ã </b></div>
                <div className="text">{course.map((cour)=>(
                    <div className="alluse" key={cour.ID}>
                        <a>{cour.ID} = {cour.name} {cour.price} {cour.typeid} {cour.authorid}</a>
                        <button onClick={() =>hendelDeletecourse(cour.ID)}>del</button>
                        <button><Link className='butupdata' to={`/updatacourse/${cour.ID}`}>updata</Link></button>   
                    </div>
                ))}
                </div>
                <button className='butadd scale'><Link className='butadd' to={"/addcours"}>–î–æ–±–≤–∏—Ç—å –∫—É—Ä—Å</Link></button>


                <div className='admin-section'><b>–∫—É–ø–ª–∏–Ω—ã–µ –∫—É—Ä—Å—ã </b></div>
                <div className="text">{buy.map((b)=>(
                    <div className="alluse" key={b.ID}>
                        <a>{b.ID} = <div key={b.id_course}>{user.name}</div> {b.id_user} {b.date}</a>
                        <button onClick={() =>hendelDeletebuy(b.ID)}>del</button>
                    </div>
                ))}
                </div>
    
           
        </div>
        )
   }else{

    return(
        <div className='errktoti'>üï∑</div>
    )
   }
}

export default Admin
