import React, { useEffect, useRef, useState } from 'react';
import "./profile.css"
import axios from 'axios';
import { useNavigate,Link } from 'react-router-dom';

function Sendcours() {

  const [user,setUser] = useState([])
  const [userid,setUserid] = useState([])
  const [user1,setUser1] = useState([])
  const [name,setName] = useState([])
  const [valid,setValid] = useState([])
  const [date,setDate] = useState([])
  const [buy,setBuy] = useState([])
  const [course,setСourse] = useState([])

  const nav = useNavigate();

  axios.defaults.withCredentials = true;
  useEffect(()=>{
    const fecthAllUser = async () =>{
        try{
          const res = await axios.get("http://localhost:8800")
          const res3 = await axios.get("http://localhost:8800/buy_course")
          const res2 = await axios.get("http://localhost:8800/course")
          const res4 = await axios.get("http://localhost:8800/User")
          setUser1(res4.data);
          setСourse(res2.data);
          setBuy(res3.data)

          setUserid(res.data.ID)
          setUser(res.data.email)
          setName(res.data.name)
          setDate(res.data.date_start_akk)
          setValid(res.data.valid)
        }catch(err){
            console.log(err)
        }
    }
    fecthAllUser()
},[])

const getUserName = (id) => {
  const use1 = user1.find((use) => use.ID === id);
  return use1 ? use1.name : 'Unknown User';
};

const getCourseTitle = (id,cid,date) => {
    const cour = course.find((courses) => courses.ID === id);

    if(cid == userid){
    return (
      <div  className="alluse">
        Вы купили: {cour.name} Дата: {date}
        <button><Link className='butupdata' to={`/course/${id}`}>Узанть больше о курсе</Link></button>  
      </div>
    );
  }else{
    return undefined;
  }
};

const form = useRef();

    if(valid){
      return (
        <div>
        <form ref={form} className="checkout-form">
            <div className="form-group">
              <label htmlFor="cardNumber">Номер карты name</label>
              <input
              name='user_name'
                type="text"
                id="cardNumber"
                required
              />
            </div>
            <div className="form-group">
              <label htmlFor="expiryDate">Дата окончания карты (MM/YY) email</label>
              <input
              name='user_email'
                type="text"
                id="expiryDate"
                required
              />
            </div>
            <div className="form-group">
              <label htmlFor="cvv">CVV subj</label>
              <input
              name='subject'
                type="text"
                id="cvv"
                required
              />
            </div>
            <div className="form-group">
              <label htmlFor="cardName">Имя на карте massage</label>
              <input
              name='message'
                type="text"
                id="cardName"
                required
              />
            </div>
            <input onClick={buycors} type="submit" className="submit-button" value="КУПИТЬ" />
           
          </form>
      </div>
    )
    }else{
      return (
       <div>
            Вы не вошли в аккаунт
        </div>
      );
    }

  
}

export default Sendcours;
