import React, { useEffect, useState } from 'react';
import "./Headercss.css"
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function Header() {

  const [user,setUser] = useState([])
  const [valid,setValid] = useState([])
  
  const nav = useNavigate();

  axios.defaults.withCredentials = true;
  useEffect(()=>{
    const fecthAllUser = async () =>{
        try{
          const res = await axios.get("http://localhost:8800")
          setUser(res.data.email)
          setValid(res.data.valid)
        }catch(err){
            console.log(err)
        }
    }
    fecthAllUser()
},[])

    const Logout = () => { 
      axios.get("http://localhost:8800/logout")
      window.location.reload()
    }

    const reg = () => {
      nav("/register")
    }
    const jemos = () => {
      nav("/")
    }
    const Anas = () => {
      nav("/info")
    }
    const Profile = () => {
      nav("/profile")
    }
  

    if(valid){
      return (
        <header className="header" >
  
              <a href='/' className='header-logo scale' onClick={jemos}>Jemos</a>
  
              <div className="header-nav">
                <button onClick={Anas}>О нас</button>
                <button onClick={Profile}>{user}</button>
                <button onClick={() => Logout()} >ВЫХОД</button>
              </div>
  
          </header>)
    }else{
      return (
        <header className="header" >

            <a href='/' className='header-logo scale' onClick={jemos}>Jemos</a>

            <div className="header-nav">
              <button onClick={Anas}>О нас</button>
              <button onClick={reg}>Регистрация</button>
            </div>

        </header>
      );
    }

  
}

export default Header;
