    id_user: "",
    date: new Date().toISOString().split('T')[0],
  });
  
  const updateUserId = (newIdUser) => {
    setBuycourse(prevState => ({
      ...prevState,
      id_user: userid,
    }));
  };

    const handleChange = (e) =>{
       
