import React, { useEffect, useState } from 'react';
import {Link} from "react-router-dom"
import axios from "axios"
import "./Admin.css";

function Admin() {
    const [user,setUser] = useState([])
    
    useEffect(()=>{
        const fecthAllUser = async () =>{
            try{
                const res = await axios.get("http://localhost:8800/User")
                setUser(res.data);
            }catch(err){
                console.log(err)
            }
        }
        fecthAllUser()
    },[])
    
    const hendelDelete = async (ID)=> {
        try{
            await axios.delete("http://localhost:8800/User/"+ID)
            console.log(ID)
            window.location.reload()
            
        }catch(err){
            console.log(err)
        }
    } 
    return (
    <div className='mainvin'>
       <div className='text'>Пользователи</div>
       <div className="text">{user.map((use)=>(
        <div className="alluse" key={use.ID}>
            <a>{use.ID} = {use.email} {use.password}</a>
            <button onClick={() =>hendelDelete(use.ID)}>del</button>
            <button><Link className='butupdata' to={"/updatacours"}>updata</Link></button>
        </div>
       ))}

       </div>
       <button className='butadd scale'><Link className='butadd' to={"/addcours"}>Добвить курс</Link></button>
    </div>
    )
}

export default Admin
