import "./xreg-log.css"
import { useNavigate } from 'react-router-dom'
import axios from 'axios';

function Login() {

  const [values,setValues] = useState();

  const nav = useNavigate();



  useEffect(()=>{
    const fecthAllUser = async () =>{
        try{
          const res = await axios.get("http://localhost:8800")
        }catch(err){
            console.log(err)
        }
    }
    fecthAllUser()
},[])

const handleChange = (event) => {
  setValues(prev => ({...prev, [event.target.name]:[event.target.value]} ))
}

const handlClick = async e =>{
  e.preventDefault()
  try{
    await axios.post("http://localhost:8800/login", values)
    .then(res => {
    if(res.data.valid){
      alert("yse")
    }else{
      alert("No")
    }
  })
    //nav("/");
  }catch(err){

  }
}

    return (
      <div className='mainwin'>
      <h1>Войти</h1>
      <input onChange={handleChange} name='email' placeholder='Почта'/><br/>
      <input onChange={handleChange} name='password' placeholder='Пароль'/><br/>
      <button type='' onClick={handlClick} className='butnext'>ВХОД</button>
      <div>Не зарегестрирован? зерегестрируйся!</div>
      <a href='/register'>Регистрация</a>
      </div>
    )
}

export default Login
