import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import './Admin.css';

function Admin() {
  const [user, setUser] = useState([]);
  const [author, setAuthor] = useState([]);
  const [type, setType] = useState([]);
  const [course, setCourse] = useState([]);
  const [admtrue, setAdmtrue] = useState([]);
  const [buy, setBuy] = useState([]);
  const [buydate, setBuydate] = useState([]);
  const [searchUser, setSearchUser] = useState('');
  const [searchCourse, setSearchCourse] = useState('');
  const [searchAuthor, setSearchAuthor] = useState('');

  axios.defaults.withCredentials = true;
  
  useEffect(() => {
    const fetchAllUser = async () => {
      try {
        const res = await axios.get('http://localhost:8800/author');
        const res2 = await axios.get('http://localhost:8800');
        const res3 = await axios.get('http://localhost:8800/buy_course');
        const res4 = await axios.get('http://localhost:8800/date_course');
        setBuydate(res4.data);
        setBuy(res3.data);
        setAdmtrue(res2.data.role);
        setAuthor(res.data);
      } catch (err) {
        console.log(err);
      }
    };
    fetchAllUser();
  }, []);

  useEffect(() => {
    const fetchAllUser = async () => {
      try {
        const res = await axios.get('http://localhost:8800/User');
        setUser(res.data);
      } catch (err) {
        console.log(err);
      }
    };
    fetchAllUser();
  }, []);

  useEffect(() => {
    const fetchAllUser = async () => {
      try {
        const res = await axios.get('http://localhost:8800/type');
        setType(res.data);
      } catch (err) {
        console.log(err);
      }
    };
    fetchAllUser();
  }, []);

  useEffect(() => {
    const fetchAllUser = async () => {
      try {
        const res = await axios.get('http://localhost:8800/course');
        setCourse(res.data);
      } catch (err) {
        console.log(err);
      }
    };
    fetchAllUser();
  }, []);

  const handleDelete = async (ID) => {
    try {
      await axios.delete(`http://localhost:8800/User/${ID}`);
      window.location.reload();
    } catch (err) {
      console.log(err);
    }
  };

  const handleDeleteAuthor = async (ID) => {
    try {
      await axios.delete(`http://localhost:8800/author/${ID}`);
      window.location.reload();
    } catch (err) {
      console.log(err);
    }
  };

  const handleDeleteType = async (ID) => {
    try {
      await axios.delete(`http://localhost:8800/type/${ID}`);
      window.location.reload();
    } catch (err) {
      console.log(err);
    }
  };

  const handleDeleteCourse = async (ID) => {
    try {
      await axios.delete(`http://localhost:8800/course/` + ID);
      window.location.reload();
    } catch (err) {
      console.log(err);
    }
  };

  const handleDeleteBuy = async (ID) => {
    try {
      await axios.delete(`http://localhost:8800/buy/${ID}`);
      window.location.reload();
    } catch (err) {
      console.log(err);
    }
  };

  const getUserName = (id) => {
    const use1 = user.find((use) => use.ID === id);
