import React, { useEffect, useState } from 'react';
import "./Headercss.css"
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function Header() {

  const [user,setUser] = useState([])
  const [valid,setValid] = useState([])
  
  const nav = useNavigate();

  axios.defaults.withCredentials = true;
  useEffect(()=>{
    const fecthAllUser = async () =>{
        try{
          const res = await axios.get("http://localhost:8800")
          setUser(res.data.email)
          setValid(res.data.valid)
        }catch(err){
            console.log(err)
        }
    }
    fecthAllUser()
},[])

    const Logout = () => { 
      axios.get("http://localhost:8800/logout")
      window.location.reload()
    }

    const reg = () => {
      nav("/register")
    }
  

    if(valid){
      return (
        <header>
          <div className="logo">
            <a className='main scale' href="/">Jemos</a>
          </div>
          <div>
            <a className='info scale' href='/info'> О нас</a>
          </div>
          <div className="register">
            <a className="reg scale">{user}</a>
          </div>
          <div>
          <button className="scale" onClick={() => Logout()} >ВЫХОД</button>
          </div>
        </header>
      );
    }else{
      return (
        <header className="header" >
          <div className="header-logo">
          <a className='main scale' href="/">Jemos</a>
          </div>
            <div className="header-nav">
            <button href='/info'> О нас</button>
            <button onClick={reg}>Регистрация</button>
            </div>
        </header>
      );
    }

  
}

export default Header;
