import React, { useEffect, useState } from 'react'
import "./Admin.css";
import axios from 'axios';
import { useLocation, useNavigate } from 'react-router-dom';

function Updatacourse() {
  const [cours,setCours] = useState({
    name:"",
    price:"",
    date: "",
    ID_type:"",
    ID_author: "",
    descriptions: "",
    img: "",
  });
  const [onecourse,setСourse] = useState([])
  const [author,setAuthor] = useState([])
  const [type,setType] = useState([])
  
  const nav = useNavigate();
  const location = useLocation();
  const courseid = location.pathname.split("/")[2];

  useEffect(()=>{
    const fecthAllUser = async () =>{
        try{
            const res = await axios.get("http://localhost:8800/author")
            setAuthor(res.data);

            const res1 = await axios.get("http://localhost:8800/type")
            setType(res1.data);

            const res2 = await axios.get("http://localhost:8800/course/" + courseid)
            setСourse(res2.data);      
          

        }catch(err){
            console.log(err)
        }
    }
    fecthAllUser()
},[])


  const handleChange = (e) =>{
    
    setCours((prev)=>({...prev,[e.target.name]: e.target.value}))
    console.log(e)
  };


  const handlClick = async e =>{
    e.preventDefault()
    cours["ID_type"] = document.getElementsByName("ID_type")[0].value
    cours["ID_author"] = document.getElementsByName("ID_author")[0].value
    try{
      await axios.put("http://localhost:8800/course/" + courseid, cours)
      console.log(cours);
      nav("/admin");
    }catch(err){
      console.log(err)
    }
  }

  return (
    <div className='mainvin2'>

       
        {onecourse.map((onecou)=>(
            <div key={onecou.ID}>
        <h1>Обновить курс {onecou.name}</h1>
            <div className='form-group'>
            <input type='text' onChange={handleChange} placeholder='Название' name='name' />
          </div>
          <div className='form-group'>
        <input type='text' onChange={handleChange} placeholder='Цена' name='price' />
      </div>
          <div className='form-group'>
            <select onChange={handleChange} size="1" name="ID_type">
            {type.map((ty) => (
                <option key={ty.ID} value={ty.ID}> {ty.name} </option>
            ))}
            </select>
        </div>
          <div className='form-group'>
            <input type='date' onChange={handleChange} name='date' />
          </div>
        <div className='form-group'>
        <select onChange={handleChange} size="1" name="ID_author">
        {author.map((aut) => (
            <option key={aut.ID} value={aut.ID}> {aut.name} </option>
        ))}
        </select>
        </div>
            <div className='form-group'>
            <input type='text' onChange={handleChange} placeholder='Описание' name='descriptions' />
          </div>
        </div>
        ))}
        <button className='butnext' onClick={handlClick}>Обновить</button>
        
    </div>
  )
}

export default Updatacourse

