import React, { useEffect, useState } from 'react'
import "./OneCoursecss.css"
import axios from 'axios';
import { useLocation } from 'react-router-dom';

function OneCourse() {
  const [course, setCourse] = useState([])

  const location = useLocation();
  const courseid = location.pathname.split("/")[2];

  axios.defaults.withCredentials = true;
  useEffect(()=>{
    const fecthAllUser = async () =>{
        try{
            const res = await axios.get("http://localhost:8800/course/" + courseid)
            setCourse(res.data);
        }catch(err){
            console.log(err)
        }
    }
    fecthAllUser()
},[])

    return (   
        <div className='mainvin'>
        {course.map((cour) => (
          <div key={cour.ID}>
            <div style={{fontSize: "56px"}}>{cour.name}</div>
            <div style={{fontSize: "24px"}}>{cour.price} рублей</div>
            <div>{cour.descriptions}</div>
        </div>
        ))}
      </div>
    )
}

export default OneCourse
