import React, { useEffect, useRef, useState } from 'react';
import "./buycours.css"
import axios from 'axios';
import {useLocation, useNavigate } from 'react-router-dom';
import emailjs from '@emailjs/browser';

function Buycours() {


  const [valid,setValid] = useState([])
  const [username,setUsername] = useState([])
    const [userid,setUserid] = useState([]) 
    const [course,setCourse] = useState([]) 
    const [info,setInfo] = useState({
        subject: "123",
        user_name: "SSS",
        cours: "it",
        user_email: "phstrudel@gmail.com",
        message: "YSEESESE",
    })
    const nav = useNavigate();

  const [buycourse,setBuycourse] = useState({
    id_course: "",
    id_user: "",
    date: "",//new Date().toISOString().slice(0, 19).replace('T', ' ')
  });



  axios.defaults.withCredentials = true;
  useEffect(()=>{
    const fecthAllUser = async () =>{
        try{
          const res = await axios.get("http://localhost:8800")
          const res1 = await axios.get("http://localhost:8800/course/" + courseId)

          setUsername(res.data.name)
          
          setCourse(res1.data);
          setValid(res.data.valid);
          setUserid(res.data.ID);

        }catch(err){
            console.log(err)
        }
    }
    fecthAllUser()

// Устанавливаем текущую дату и время при загрузке компонента
const currentDate = new Date();
const formattedDate = currentDate.getFullYear() + '-' + 
                      String(currentDate.getMonth() + 1).padStart(2, '0') + '-' + 
                      String(currentDate.getDate()).padStart(2, '0') + ' ' + 
                      String(currentDate.getHours()).padStart(2, '0') + ':' + 
                      String(currentDate.getMinutes()).padStart(2, '0') + ':' + 
                      String(currentDate.getSeconds()).padStart(2, '0');
setBuycourse(prevState => ({
  ...prevState,
  date: formattedDate
}));



},[])

const location = useLocation();
  const queryParams = new URLSearchParams(location.search);
  const courseId = queryParams.get('courseId');


useEffect(() => {
    
    if (userid && courseId) {
      setBuycourse(prevState => ({
        ...prevState,
        id_course: courseId,
        id_user: userid
      }));
    }
  }, [userid, courseId]);

    const form = useRef();
  
    const sendEmail = (e) => {
      e.preventDefault();

      emailjs
      .sendForm('service_frjp6qq', 'template_0vm0blb', form.current, {
        publicKey: 'PfbGmtvViWaARzzK3',
      })
      .then(
        () => {
          console.log(form);
          console.log('SUCCESS!');
        },
        (error) => {
          console.log(form);
          console.log('FAILED...', error.text);
        },
      );

  
    };

const buycors = async e =>{
    e.preventDefault()

   

    try{
        const response = await axios.post("http://localhost:8800/buycourse", buycourse)
        //axios.post("http://localhost:8800/sendmail", info)
        console.log('Response:', response.data);    
        nav("/nicebuy")
      }catch(err){
        
      }
}

if(valid){
    return (
        <div className="checkout-form-container">
 <h2>Введите данные карты</h2>
          <from  ref={form} onSubmit={sendEmail} >
          <label>Name</label>
            <input type="text" name="user_name" />
            <label>subject</label>
            <input type="text" name="subject" />
            <label>Email</label>
            <input type="email" name="user_email" />
            <label>Message</label>
            <textarea name="message" />
            <input type="submit" value="Send" />
          </from>
    <div>{course.map((cour) => (
          <div key={cour.ID}> Покупка курса: {cour.name}<br/></div>
          ))}</div>
         

          <form className="checkout-form">
            <div className="form-group">
              <label htmlFor="cardNumber">Номер карты name</label>
              <input
                type="text"
                id="cardNumber"
                required
              />
            </div>
            <div className="form-group">
              <label htmlFor="expiryDate">Дата окончания карты (MM/YY) email</label>
              <input
                type="text"
                id="expiryDate"
                required
              />
            </div>
            <div className="form-group">
              <label htmlFor="cvv">CVV subj</label>
              <input
                type="text"
                id="cvv"
                required
              />
            </div>
            <div className="form-group">
              <label htmlFor="cardName">Имя на карте massage</label>
              <input
                type="text"
                id="cardName"
                required
              />
            </div>
            <button onClick={buycors} type="submit" className="submit-button">КУПИТЬ</button>
          </form>
        </div>
      );
  }else{
    return (
     <div>
          Войди в акаунт
      </div>
    );
  }
}
export default Buycours;
