import React, { useEffect, useState } from 'react'
import "./Admin.css";
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function Addcours() {
  const [cours,setCours] = useState({
    name:"",
    price:"",
    date: "",
    ID_type:"",
    ID_author: "",
    descriptions: "",
    img: "",
  });


  //const [img,setImg] = useState([])

  const nav = useNavigate();

  const [author,setAuthor] = useState([])
  const [type,setType] = useState([])
  const [file, setFile] = useState(null);

  useEffect(()=>{
    const fecthAllUser = async () =>{
        try{
            const res = await axios.get("http://localhost:8800/author")
            setAuthor(res.data);

            const res1 = await axios.get("http://localhost:8800/type")
            setType(res1.data);
        }catch(err){
            console.log(err)
        }
    }
    fecthAllUser()
},[])


const handleFileChange = (event) => {
  setFile(event.target.files[0]);
};


const handleFileUpload = async () => {
  const formData = new FormData();
  formData.append('file', file);

  try {
      const response = await axios.post('/upload', formData, {
          headers: {
              'Content-Type': 'multipart/form-data'
          }
      });
      console.log('File uploaded successfully:', response.data);
  } catch (error) {
      console.error('Error uploading file:', error);
  }
};




  const handleChange = (e) =>{
    setCours((prev)=>({...prev,[e.target.name]: e.target.value}))
  };


  const handlClick = async e =>{
    e.preventDefault()
    cours["ID_type"] = document.getElementsByName("ID_type")[0].value
    cours["ID_author"] = document.getElementsByName("ID_author")[0].value
    try{
      console.log(cours)
      await axios.post("http://localhost:8800/course", cours)
      
      nav("/admin");
    }catch(err){

    }
  }



  return (
    <div className='mainvin'>
        <h1>Добавить курс</h1>
        <input type='text' onChange={handleChange} placeholder='Названия' name='name'/>
        <input type='text' onChange={handleChange} placeholder='Цена' name='price'/>

        <select onChange={handleChange} size="1" name="ID_type">
        {type.map((ty) => (
            <option key={ty.ID} value={ty.ID}> {ty.name} </option>
        ))}
        </select>

        <input type='date' onChange={handleChange} name='date'/>

        <select onChange={handleChange} size="1" name="ID_author">
        {author.map((aut) => (
            <option key={aut.ID} value={aut.ID}> {aut.name} </option>
        ))}
        </select>

        <input type='text' onChange={handleChange} placeholder='Описания' name='descriptions' />

          <input type="file" onChange={handleFileChange} />
          <button onClick={handleFileUpload}>Upload</button>
       

        <button className='butnext' onClick={handlClick}>Добавить</button>
    </div>
  )
}

export default Addcours


