import React, { useEffect, useState } from 'react';
import {Link} from "react-router-dom"
import axios from "axios"
import "./Admin.css";

function Admin() {
    //чтения бд
    const [user,setUser] = useState([])
    const [author,setAuthor] = useState([])
    const [type,setType] = useState([])
    const [course,setСourse] = useState([])
    
    useEffect(()=>{
        const fecthAllUser = async () =>{
            try{
                const res = await axios.get("http://localhost:8800/author")
                setAuthor(res.data);
            }catch(err){
                console.log(err)
            }
        }
        fecthAllUser()
    },[])


    useEffect(()=>{
        const fecthAllUser = async () =>{
            try{
                const res = await axios.get("http://localhost:8800/User")
                setUser(res.data);
            }catch(err){
                console.log(err)
            }
        }
        fecthAllUser()
    },[])

    useEffect(()=>{
        const fecthAllUser = async () =>{
            try{
                const res = await axios.get("http://localhost:8800/type")
                setType(res.data);
            }catch(err){
                console.log(err)
            }
        }
        fecthAllUser()
    },[])

    useEffect(()=>{
        const fecthAllUser = async () =>{
            try{
                const res = await axios.get("http://localhost:8800/course")
                setСourse(res.data);
            }catch(err){
                console.log(err)
            }
        }
        fecthAllUser()
    },[])
    //удаления 
    const hendelDelete = async (ID)=> {
        try{
            await axios.delete("http://localhost:8800/User/"+ID)
            console.log(ID)
            window.location.reload()
            
        }catch(err){
            console.log(err)
        }
    } 

    const hendelDeleteauthor = async (ID)=> {
        try{
            await axios.delete("http://localhost:8800/author/"+ID)
            console.log(ID)
            window.location.reload()
            
        }catch(err){
            console.log(err)
        }
    } 

    const hendelDeletetype = async (ID)=> {
        try{
            await axios.delete("http://localhost:8800/type/"+ID)
            console.log(ID)
            window.location.reload()
            
        }catch(err){
            console.log(err)
        }
    } 

    //клиент
    return (
    <div className='mainvin'>
       <div className='text'><b>Пользователи</b></div>
            <div className="text">{user.map((use)=>(
                <div className="alluse" key={use.ID}>
                    <a>{use.ID} = {use.email} {use.password} {use.role}</a>
                    <button onClick={() =>hendelDelete(use.ID)}>del</button>
                    <button><Link className='butupdata' to={`/updatauser/${use.ID}`}>updata</Link></button>
                </div>
            ))}
            </div>

            <div className='text'><b>Авторы курсов</b></div>
            <div className="text">{author.map((aut)=>(
                <div className="alluse" key={aut.ID}>
                    <a>{aut.ID} = {aut.name} {aut.surname} {aut.descriptions}</a>
                    <button onClick={() =>hendelDeleteauthor(aut.ID)}>del</button>
                    <button><Link className='butupdata' to={`/updataauthor/${aut.ID}`}>updata</Link></button>   
                </div>
            ))}
            </div>
            <button className='butadd scale'><Link className='butadd' to={"/addauthor"}>Добвить автора</Link></button>

            <div className='text'><b>Тип курса</b></div>
            <div className="text">{type.map((ty)=>(
                <div className="alluse" key={ty.ID}>
                    <a>{ty.ID} = {ty.name} </a>
                    <button onClick={() =>hendelDeletetype(ty.ID)}>del</button>
                    <button><Link className='butupdata' to={`/updataauthor/${ty.ID}`}>updata</Link></button>   
                </div>
            ))}
            </div>
            <button className='butadd scale'><Link className='butadd' to={"/addtype"}>Добвить типа курса</Link></button>

            <div className='text'><b>Курсы </b></div>
            <div className="text">{course.map((cour)=>(
                <div className="alluse" key={cour.ID}>
                    <a>{cour.ID} = {cour.name} {cour.price} {cour.typeid}</a>
                    <button onClick={() =>hendelDeletetype(cour.ID)}>del</button>
                    <button><Link className='butupdata' to={`/updataauthor/${cour.ID}`}>updata</Link></button>   
                </div>
            ))}
            </div>
            <button className='butadd scale'><Link className='butadd' to={"/addtype"}>Добвить типа курса</Link></button>
       
    </div>
    )
}

export default Admin
