// OneCourse.js
import React, { useEffect, useState } from 'react';
import './OneCoursecss.css';
import axios from 'axios';
import { useLocation, Link } from 'react-router-dom';
import { useParams, useNavigate } from 'react-router-dom';

function OneCourse() {
  const [course, setCourse] = useState([]);
  const [author, setAuthor] = useState([]);

  const location = useLocation();
  const courseid = location.pathname.split("/")[2];

  axios.defaults.withCredentials = true;

  useEffect(() => {
    const fetchAllUser = async () => {
      try {
        const res = await axios.get(`http://localhost:8800/course/${courseid}`);
        setCourse(res.data);
        const res1 = await axios.get("http://localhost:8800/author");
        setAuthor(res1.data);
      } catch (err) {
        console.log(err);
      }
    }
    fetchAllUser();
  }, [courseid]);

  const getAuthor = (id) => {
    const aut1 = author.find((aut) => aut.ID === id);
    return aut1 ? aut1.name : 'Unknown User';
  };

  const navigate = useNavigate();

  const handleBuyCourse = () => {
    navigate(`/buycours?courseId=${courseid}`);  // Перенаправляем на страницу покупки с query параметром
  };

  const handlelinkCourse = () => {
    navigate(`/linkcours?courseId=${courseid}`);  // Перенаправляем на страницу записи с query параметром
  };

  return (
    <div className='main-container'>
      {course.map((cour) => (
        <div key={cour.ID} className='course-container'>
          <div className='left-side'>
            <div className='course-name'>{cour.name}</div>
            <div className='course-description-box'>
              <div className='description-title'>Описание курса:</div>
              <div className='course-description'>{cour.descriptions}</div>
            </div>
            <div className='course-price'>{cour.price} рублей</div>
            <div className='buttons'>
              <button className='buy-button' onClick={handleBuyCourse}>Купить</button>
              <button className='enroll-button' onClick={handlelinkCourse}>Записаться на онлайн обучение</button>
            </div>
          </div>
          <div className='right-side'>
            <img className='course-image' src={`http://localhost:8800/course/image/${cour.ID}`} alt={cour.name} />
            <div className='author-info'>
              <div>Автор курса:</div>
              <button className='author-button'>
                <Link to={`/authorpage/${cour.ID_author}`}>{getAuthor(cour.ID_author)}</Link>
              </button>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
}

export default OneCourse;
