import React, { useEffect, useState } from 'react'
import "./Admin.css";
import axios from 'axios';
import { useLocation } from 'react-router-dom';

function Updatacourse() {
  const [cours,setCours] = useState({
    name:"",
    price:"",
    img: "",
    typeid:"",
    authorid: "",
    descriptions: "",
  });
  const [onecourse,setСourse] = useState([])
  const [author,setAuthor] = useState([])
  const [type,setType] = useState([])
  
  const location = useLocation();
  const courseid = location.pathname.split("/")[2];


  useEffect(()=>{
    const fecthAllUser = async () =>{
        try{
            const res = await axios.get("http://localhost:8800/author")
            setAuthor(res.data);

            const res1 = await axios.get("http://localhost:8800/type")
            setType(res1.data);

            const res2 = await axios.get("http://localhost:8800/course/" + courseid)
            setСourse(res2.data);          

        }catch(err){
            console.log(err)
        }
    }
    fecthAllUser()
},[])


  const handleChange = (e) =>{
    
    setCours((prev)=>({...prev,[e.target.name]: e.target.value}))
    console.log(e)
  };


  const handlClick = async e =>{
    e.preventDefault()
    cours["typeid"] = document.getElementsByName("typeid")[0].value
    cours["authorid"] = document.getElementsByName("authorid")[0].value
    try{
      console.log(cours)
      await axios.post("http://localhost:8800/course", cours)
    }catch(err){

    }
  }

  return (
    <div className='mainvin'>
        {onecourse.map((onecou)=>(
            <div key={onecou.ID}>
        <h1>Обновить курс {onecou.name}</h1>
        <input type='text' onChange={handleChange} placeholder={onecou.name} name='name'/>
        <input type='text' onChange={handleChange} placeholder={onecou.price} name='price'/>

        <select onChange={handleChange} size="1" name="typeid">
        {type.map((ty) => (
            <option key={ty.ID} value={ty.ID}> {ty.name} </option>
        ))}
        </select>

        <select onChange={handleChange} size="1" name="authorid">
        {author.map((aut) => (
            <option key={aut.ID} value={aut.ID}> {aut.name} </option>
        ))}
        </select>

        <input type='text' onChange={handleChange} placeholder='Описания' name='descriptions' />
        <input type='file' onChange={handleChange} placeholder='Обложка курса' name='img'/>
        </div>
        ))}
        <button className='butnext' onClick={handlClick}>Обновить</button>
        
    </div>
  )
}

export default Updatacourse
