import React, { useEffect, useState } from 'react';
import {Link, useNavigate} from "react-router-dom"
import axios from "axios"
import './App.css';
import Admin from './adminpages/Admin';


function App() {
  const [course,setСourse] = useState([])
  const [value, setValuse] = useState('')
  const [role,setRole] = useState('')

  const nav = useNavigate();

  const filteredCourse = course.filter(cours => {
    return cours.name.toLowerCase().includes(value.toLowerCase())
  })


  axios.defaults.withCredentials = true;
  useEffect(()=>{
    const fecthAllUser = async () =>{
        try{
          const res2 = await axios.get('http://localhost:8800')
          .then(res2 => {
            if(res2.data.valid){
              setRole(res2.data.role)
            }
            else{
              nav('/login')
            }
          })
          const res = await axios.get("http://localhost:8800/course")
          setСourse(res.data);

        }catch(err){
            console.log(err)
        }
    }
    fecthAllUser()
},[])

  return (
    <div className='mainwin'>

     <div className='mainvin'>
        <input type="text" onChange={(event) => setValuse(event.target.value)} placeholder="Поиск..." />
        <button type="submit">Поиск</button>
        </div>
        {role == "adm" && <Admin />}
      <div>{filteredCourse.map((cour) => (
        <div className='onecourse' key={cour.ID}>
          <div>{cour.name}</div>
          <div>{cour.price}</div>
          <button><Link className='butupdata' to={`/course/${cour.ID}`}>Узанть больше о курсе</Link></button>   
          </div>
      ))}</div>
    </div>
  );
}

export default App;
