import React, { useEffect, useState } from 'react';
import "./Headercss.css"
import Cookies from "universal-cookie"
import axios from 'axios';

function Header() {

  const [user,setUser] = useState([])
  const [valid,setValid] = useState([])
  axios.defaults.withCredentials = true;
  useEffect(()=>{
    const fecthAllUser = async () =>{
        try{
          const res = await axios.get("http://localhost:8800")
          setUser(res.data.email)
          setValid(res.data.valid)
        }catch(err){
            console.log(err)
        }
    }
    fecthAllUser()
},[])

   const cookies = new Cookies();

    const Logout = () => {   
      cookies.remove("user");
      window.location.reload()
    }
  

    if(valid){
      return (
        <header>
          <div className="logo">
            <a className='main scale' href="/">Jemos</a>
          </div>
          <div>
            <a className='info scale' href='/info'> О нас</a>
          </div>
          <div className="register">
            <a className="reg scale">{user}</a>
          </div>
          <div>
          <button className="scale" onClick={() => Logout()} >ВЫХОД</button>
          </div>
        </header>
      );
    }else{
      return (
        <header>
          <div className="logo">
            <a className='main scale' href="/">Jemos</a>
          </div>
          <div>
            <a className='info scale' href='/info'> О нас</a>
          </div>
          <div className="register">
            <a className="reg scale" href='/register' >Регистрация</a>
            
          </div>
        </header>
      );
    }

  
}

export default Header;
