import React, { useEffect, useState } from 'react'
import "./OneCoursecss.css"
import axios from 'axios';
import { useLocation, Link } from 'react-router-dom';
import { useParams, useNavigate } from 'react-router-dom';

function OneCourse() {
  const [course, setCourse] = useState([])

  const location = useLocation();
  const courseid = location.pathname.split("/")[2];

  axios.defaults.withCredentials = true;
  useEffect(()=>{
    const fecthAllUser = async () =>{
        try{
            const res = await axios.get("http://localhost:8800/course/" + courseid)
            setCourse(res.data);
        }catch(err){
            console.log(err)
        }
    }
    fecthAllUser()
},[])


  const { id } = useParams();  // Получаем номер курса из URL
  const history = useNavigate();

  const handleBuyCourse = () => {
    history.push(`/buycours?courseId=${id}`);  // Перенаправляем на страницу покупки с query параметром
    console.log(id)
  }


    return (   
        <div className='mainvin'>
        {course.map((cour) => (
          <div key={cour.ID}>
            <div style={{fontSize: "56px"}}>{cour.name}</div>
            <div style={{fontSize: "24px"}}>{cour.price} рублей</div>
            <div>{cour.descriptions}</div>
            <div></div>
            <button onClick={handleBuyCourse}>купить</button>
        </div>
        ))}
      </div>
    )
}

export default OneCourse
